mapper.select:
select id, boardTitle, boardWriter, boardContents,date_format(createdAt, "%Y-%m-%d") as createdAt
from board_table order by id desc
LIMIT #{limit}, #{offset}

select id, boardTitle, boardWriter, boardContents,
date_format(createdAt, "%Y-%m-%d %H:%i:%s") as createdAt
from board_table where id=#{id}
